import axios from "axios";
import { useEffect, useState } from "react";


const Users = () => {

    const [users, setUsers] = useState([]);
    const [confirmationPopUp, setConfirmationPopUp] = useState({
        id: 0,
        message: "",
        isPopUp: false,
    });

    console.log("users", users);

    useEffect(()=>{
        axios.get("http://localhost:3005/users/all-users")
        .then((response)=>setUsers(response.data))
    },[]) 
    
    const confirmDelete = (toRemoveUser, index) => {
        //isPopUp == true to render the pop up.
        setConfirmationPopUp((prevData)=>({
            ...prevData,
            id: toRemoveUser.id,
            isPopUp: true,
            message: `Are you sure you want to delete user ${toRemoveUser.id}`
        }));
    }

    const confirmed = () => {
        console.log("confirmed user id", confirmationPopUp.user_id)
        // setConfirmationPopUp((prevData)=>({...prevData, isConfirmed: true}));
        removeUser(confirmationPopUp);
    }
    const removeUser = async (toRemoveUser) => {
            //Pop up module to confirm user delete
                       
                await axios.delete(`http://localhost:3005/users/delete/${toRemoveUser.id}`)
                .then(()=>{
                    console.log("User id deleted", toRemoveUser)
                    const newUsers = users.filter((user)=>user.id != toRemoveUser.id);
                    console.log("----------------new users----------------", newUsers);
                    setUsers(newUsers);
                    setConfirmationPopUp((prevData)=>({...prevData, isConfirmed: false}));

                }); 
            if(!confirmationPopUp.isConfirmed){
                setConfirmationPopUp((prevData)=>({...prevData, isPopUp: false}))
                return
            }
            // console.log("Did not run test")
            
    }

    const mappedUsers = users.map((user, index)=>{
        return (
           <tr key={index}>
            <td>
                {user.username}
            </td>
            <td>
                {user.email}
            </td>
            <button onClick={()=>confirmDelete(user, index)}>Remove</button>
            <button>Edit</button>
           </tr>
        )
    }) 
    console.log("users", users);

    return(
        <table className="users-table">
            <thead>
                <td>
                    Username
                </td>
                <td>
                    Email
                </td>
            </thead>
            <tbody>
                 {mappedUsers}
                 {confirmationPopUp.isPopUp ? 
                <div className="confirmationPopUp">
                    {confirmationPopUp.message}
                    <button onClick={confirmed}>Yes</button>
                    <button  onClick={()=>setConfirmationPopUp((prevData)=>({...prevData, isPopUp: false}))}>No</button>
                </div> 
                : null
            }
            </tbody>
          
        </table>
    )
}

export default Users;
