import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Register = () => {
    //Initiate state variable for users 
    const [user, setUser] = useState({
        username: "",
        email: "",
        password: ""
    })

    const [validUserName, setValidUserName] = useState(false);

    //State variables for Error message pop ups.
    const [errorMessage, setErrorMessage] = useState("");
    const [missingField, setMissingField] = useState(false);

    useEffect(() => {
        setErrorMessage("");
    }, [user]);

    useEffect(() => {
        const result = USERNAMEREGEX.test(user.username);
        console.log(result);
        setValidUserName(result);
    }, [user.username]);

    //Watches changes in the form.
    const handleChange = (e) => {
        const { name, value } = e.target
        setUser((prevData) => ({ ...prevData, [name]: value }));
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:3005/users/new-user", user);
        } catch (e) {

        }

    }
    return (
        <div className="formWrapper">
            <Link to="/">back</Link>
            <h1>Register</h1>
            <form onSubmit={(e) => handleSubmit(e)}>

                <label htmlFor="username">Enter Username</label>
                <input
                    type="text"
                    autoComplete="off"
                    value={user.username}
                    name="username"
                    required
                    onChange={handleChange}
                />
                <p className={user.username && !validUserName ? "instructions" : "hideInstructions"}>"4 to 24 characters. Must begin with a letter. Letters, numbers, underscores, hyphens allowed."</p>
                <label htmlFor="username">Enter email</label>
                <input
                    type="email"
                    autoComplete="off"
                    value={user.email}
                    name="email"
                    required
                    onChange={handleChange}
                />
                <p className={user.username && !validUserName ? "instructions" : "hideInstructions"}>"4 to 24 characters. Must begin with a letter. Letters, numbers, underscores, hyphens allowed."</p>
                <label htmlFor="password">Enter Password</label>
                <input
                    type="password"
                    value={user.password}
                    name="password"
                    required
                    onChange={handleChange}
                />
                <button id="register-btn" className="submit-btn" >Register</button>
                <p className="errorMessage">{errorMessage.length > 0 ? errorMessage : ""}</p>
            </form>
        </div>
    )
}

export default Register;
