import axios from "axios";
import { useEffect, useState } from "react";


const Users = () => {

    const [users, setUsers] = useState([]);
    const [confirmationPopUp, setConfirmationPopUp] = useState({
        message: "",
        isConfirmed: false
    });

    console.log("users", users);

    useEffect(()=>{
        axios.get("http://localhost:3005/users/all-users")
        .then((response)=>setUsers(response.data))
    },[]) 
    
    const removeUser = async (id, index) => {

       await axios.delete(`http://localhost:3005/users/delete/${id}`)
        .then(()=>{
            console.log("User id deleted", id)
            const newUsers = users.filter((user)=>user.id != id);
            console.log("----------------new users----------------", newUsers);
            setUsers(newUsers);
        })
    }

    const mappedUsers = users.map((user, index)=>{
        return (
           <tr key={index}>
            <td>
                {user.username}
            </td>
            <td>
                {user.email}
            </td>
            <button onClick={()=>removeUser(user.id, index)}>Remove</button>
            <div className="confirmationPopUp">{confirmationPopUp.isConfirmed ? confirmationPopUp.message : null}</div>
            <button>Edit</button>
           </tr>
        )
    }) 
    console.log("users", users);

    return(
        <table className="users-table">
            <thead>
                <td>
                    Username
                </td>
                <td>
                    Email
                </td>
            </thead>
            <tbody>
                 {mappedUsers}
            </tbody>
          
        </table>
    )
}

export default Users;
