            console.log("----------------new users----------------", newUsers);
            setUsers(newUsers);
        })
    }

    const mappedUsers = users.map((user, index)=>{
        return (
           <tr key={index}>
            <td>
                {user.username}
            </td>
            <td>
                {user.email}
            </td>
            <button onClick={()=>removeUser(user, index)}>Remove</button>
            {confirmationPopUp.isPopUp ? 
